import "./App.css";
import { studentsData } from "./data";
import React, { useEffect, useState } from "react";
import "./Feed.css";

// Separate AlumniCard Component
const AlumniCard = ({ name, degree, year, linkedIn, profilePic }) => {
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      const url =
        "https://li-data-scraper.p.rapidapi.com/search-people?keywords=max&start=0";
      const options = {
        method: "GET",
        headers: {
          "x-rapidapi-key": "cbd577779amsh45be24639b52454p188560jsn4e018f72d094",
          "x-rapidapi-host": "li-data-scraper.p.rapidapi.com",
        },
      };

      setLoading(true);
      setError(null);

      try {
        const response = await fetch(url, options);

        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }

        const result = await response.json(); // Ensure we parse JSON
        console.log("Fetched data:", result);
        setProfile(result); // Assuming API returns useful profile data
      } catch (error) {
        console.error("Error fetching profile data:", error.message);
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, []);

  if (loading) return <p>Loading profile data...</p>;
  if (error) return <p>Error: {error}</p>;

  return (
    <div className="card">
      <div className="card-content">
        <img
          src={profilePic}
          alt={`${name}'s Profile`}
          className="profile-pic"
        />
        <h3 className="student-name">{name}</h3>
        <p className="student-degree">{degree}</p>
        <p className="student-year">Class of {year}</p>
        <a
          href={linkedIn}
          target="_blank"
          rel="noopener noreferrer"
          className="linkedin-button"
        >
          Connect on LinkedIn
        </a>
      </div>
    </div>
  );
};

function Feed() {
  const [selectedCollege, setSelectedCollege] = useState("");
  const [students, setStudents] = useState([]);

  const handleCollegeChange = (e) => {
    const college = e.target.value;
    setSelectedCollege(college);
    setStudents(studentsData[college] || []);
  };

  return (
    <div className="App">
      <div className="alumni-finder">
        <h1>Alumni Finder</h1>
        <label htmlFor="college-select">Select College: </label>
        <select
          id="college-select"
          onChange={handleCollegeChange}
          value={selectedCollege}
        >
          <option value="">--Choose a College--</option>
          {Object.keys(studentsData).map((college) => (
            <option key={college} value={college}>
              {college}
            </option>
          ))}
        </select>

        <div className="alumni-list">
          <h2>Alumni List</h2>
          {students.length > 0 ? (
            <div className="card-container">
              {students.map((student, index) => (
                <AlumniCard
                  key={index}
                  name={student.name}
                  degree={student.degree}
                  year={student.year}
                  linkedIn={student.linkedIn}
                  profilePic={student.profilePic} // Assuming each student has a profilePic field
                />
              ))}
            </div>
          ) : (
            <p>No students available for this college.</p>
          )}
        </div>
      </div>
    </div>
  );
}

export default Feed;
